&lt;cfscript&gt; 
filename = &quot;log.txt&quot;; 
    try { 
    result = FileOpen(expandpath(filename)); 
    WriteDump(result); 
} 
catch(Expression exception) { 
    WriteOutput(&quot;&lt;p&gt;An Expression exception was thrown.&lt;/p&gt;&quot;); 
    WriteOutput(&quot;&lt;p&gt;#exception.message#&lt;/p&gt;&quot;); 
    WriteLog(type=&quot;Error&quot;, file=&quot;myapp.log&quot;, text=&quot;[exception.type] 
    #exception.message#&quot;); 
    } 
&lt;/cfscript&gt;<br>
