SYNTAX:
&lt;cfmap 
        centeraddress=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">address</i>&quot; 
        centerlatitude=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">latitude in degrees</i>&quot; 
        centerlongitude=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">longitude in degrees</i>&quot; 
        collapsible=&quot;true|false&quot; 
        continuouszoom=&quot;true|false&quot; 
        doubleclickzoom=&quot;true|false&quot; 
        height=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">integer</i>&quot; 
        hideborder=&quot;true|false&quot; 
        initshow=&quot;true|false&quot; 
        markerbind=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">bind expression</i>&quot; 
        markercolor=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">marker color</i>&quot; 
        markericon=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">icon path</i>    &quot; 
        markerwindowcontent=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">content</i>&quot; 
        name=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">name</i>&quot; 
        onerror=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">JavaScript function name</i>&quot; 
        onload=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">JavaScript function name</i>&quot; 
        overview=&quot;true|false&quot; 
        scrollwheelzoom=&quot;true|false&quot; 
        showallmarkers=&quot;true|false&quot; 
        showcentermarker=&quot;true|false&quot; 
        showmarkerwinodw=&quot;true|false&quot; 
        showscale=&quot;true|false&quot; 
        tip=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">center property marker tips</i>&quot; 
        title=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">string</i>&quot; 
        type=&quot;map|satellite|hybrid|earth|terrain&quot; 
        typecontrol=&quot;none|basic|advanced&quot; 
        width=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">integer</i>&quot; 
        zoomcontrol=&quot;none|small|large|small3d|large3d&quot; 
        zoomlevel=&quot;<i xmlns:fn="http://www.w3.org/2005/xpath-functions" xmlns:fo="http://www.w3.org/1999/XSL/Format" xmlns:xs="http://www.w3.org/2001/XMLSchema">integer</i>&quot;&gt; 
&lt;/cfmap&gt;<br>

