    centerlongitude=&quot;42.339641&quot; 
    doubleclickzoom=&quot;true&quot; 
    overview=&quot;true&quot; 
    scrollwheelzoom=&quot;true&quot; 
    showscale=&quot;true&quot; 
    tip=&quot;My Map&quot; 
    zoomlevel=&quot;4&quot;/&gt;<br>
